mapper.select:

select * FROM sys_menus where 1=1 

 AND menuname LIKE CONCAT('%','${pagerHelperRQ.searchString}','%' ) 

 
 		  order by ${pagerHelperRQ.sidx}
 		   
 		     ${pagerHelperRQ.sord}
			
	
  limit #{pagerHelperRQ.start,jdbcType=INTEGER},#{pagerHelperRQ.rows,jdbcType=INTEGER}

     
     select menus.*,modelmenu.modelid,models.modelname,case when modelmenu.menuid is null then 'false' else 'true' end  checked FROM sys_menus as menus left join sys_modelmenu as modelmenu on menus.menuid=modelmenu.menuid left join sys_models as models on modelmenu.modelid=models.modelid
where 1=1 
     
      AND modelmenu.modelid= #{modelid,jdbcType=VARCHAR}

mapper.insert:
   INSERT INTO sys_menus
            (menuid,
             orderby,
             menuname,
             description,
             icon,
			 creatuser,
			 creattime,
			 modifyuser,
			 modifytime)
    values (
    UUID()
    ,#{orderby,jdbcType=INTEGER}
    ,#{menuname,jdbcType=VARCHAR}
    ,#{description,jdbcType=VARCHAR}
    ,#{icon,jdbcType=VARCHAR}
    , #{creatuser,jdbcType=VARCHAR}
    , #{creattime,jdbcType=VARCHAR}
    , #{modifyuser,jdbcType=VARCHAR}
    , #{modifytime,jdbcType=VARCHAR}
    )
